export function passwordValidator(password) {
  const strongRegex = new RegExp("^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#\$%\^&\*])(?=.{8,})");
  if (!password || password.length <= 0) return "Error: Password can't be empty"
  if (!strongRegex.test(password)) return 'Your password need : 1 lowercase alphabetical character | 1 uppercase alphabetical character | 1 numeric character | 1 special character'
  return ''
}
