export function passwordValidator(password) {
  const strong = new RegExp("^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#\$%\^&\*])(?=.{8,})");
  if (!password || password.length <= 0) return "Error: Password can't be empty"
  if (!strong.test(password)) return 'Need Capital eltter'
  return ''
}
